            // ===== [FIX 10] ‡πÅ‡∏Å‡πâ Endpoint ‡πÄ‡∏õ‡πá‡∏ô `/server/delete-many` =====
            await axios.post(`${SERVER_URL}/server/delete-many`, {
              ids: this.selectedItems
            });
            this.translations = this.translations.filter(item => !this.selectedItems.includes(item.id));
            this.selectedItems = [];
            break;
          case 'all':
            await axios.delete(`${SERVER_URL}/server/all`);
            this.translations = [];
            this.selectedItems = [];
            break;
        }
      } catch (error) {
        console.error("‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏•‡∏ö:", error);
        alert("‡∏Å‡∏≤‡∏£‡∏•‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß");
      } finally {
        this.closeDeleteConfirmModal();
      }
    },
    formatDate(dateStr) {
      return new Date(dateStr).toLocaleString("th-TH", {
        dateStyle: 'medium',
        timeStyle: 'short'
      });
    },
    getImageUrl(path) {
      // ===== [FIX 11] ‡πÅ‡∏Å‡πâ Port ‡πÄ‡∏õ‡πá‡∏ô 3000 ‡πÉ‡∏´‡πâ‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ö server =====
      // server.js ‡∏à‡∏∞ serve path ‡∏ó‡∏µ‡πà‡πÄ‡∏õ‡πá‡∏ô 'saved_images/...'
      return `${SERVER_URL}/${path}`;
    },
    showImageModal(item) {
      this.selectedImageUrl = this.getImageUrl(item.image_path);
      this.isModalVisible = true;
    },
    closeImageModal() {
      this.isModalVisible = false;
      this.selectedImageUrl = '';
    },
    async downloadImage() {
      try {
        const response = await fetch(this.selectedImageUrl);
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.download = `translation-${Date.now()}.jpg`;
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        window.URL.revokeObjectURL(url);
      } catch (error) {
        console.error("Download failed:", error);
        alert("‡πÑ‡∏°‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡∏î‡∏≤‡∏ß‡∏ô‡πå‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û‡πÑ‡∏î‡πâ");
      }
    },
    openLink() {
      window.open('https://www.facebook.com/profile.php?id=100057485296390', '_blank');
    }
  }
