import Vue from 'vue'
import App from './App.vue'
import VueRouter from 'vue-router'
import HandGestures from './views/HandGestures.vue' // import หน้าใหม่

Vue.use(VueRouter)

// กำหนด routes
const routes = [
  { path: '/', component: App },          // หน้า Home (App.vue)
  { path: '/gestures', component: HandGestures } // หน้า HandGestures
]

// สร้าง router instance
const router = new VueRouter({
  mode: 'history', // ใช้ history mode
  routes
})

// ส่ง router เข้า Vue instance
new Vue({
  router,
  render: h => h(App)
}).$mount('#app')
