import Vue from 'vue';
import App from './App.vue';
import VueRouter from 'vue-router';
import HandGestures from './HandGestures.vue';
Vue.use(VueRouter);
const routes = [{
  path: '/',
  component: App
},
// หน้าแปลภาษามือ
{
  path: '/gestures',
  component: HandGestures
} // หน้าแสดงท่ามือ
];
const router = new VueRouter({
  mode: 'history',
  routes
});
new Vue({
  router,
  render: h => h(App)
}).$mount('#app');
