import { createRouter, createWebHistory } from 'vue-router'
import App from '../App.vue'             // หน้าแปลภาษามือ (หลัก)
import HandGestures from '../HandGestures.vue' // หน้าแสดงท่ามือ

const routes = [
  { path: '/', name: 'App', component: App },
  { path: '/gestures', name: 'Gestures', component: HandGestures }
]

const router = createRouter({
  history: createWebHistory(),
  routes
})

export default router
